import React from 'react';
import './Search.css';
import axios from 'axios';

class Search extends React.Component {

    // define Search with its properties here
    constructor( props ) {
        super( props );

        this.state = {
            query: '',
            results: {
                title: '',
                yor: 0
            },
            message: ''
        }

        this.movieIDs = [];
    }

    renderSearchResults = () => {
        const { results } = this.state;
        if ( Object.keys( results ).length && results.length) {
            return (
                <div className="result-container">
                    {results.Title}
                </div>
            )
        }
    }

    fetchSearchResult = ( event ) => {
        const query = this.state.query;
        const searchURL = `http://www.omdbapi.com/?apikey=7353c7c5&t=${ query }`;
        axios.get( searchURL )
            .then( res => {
                console.warn(res.data);
                if ( res.data.Title ) {
                    this.setState( {
                        results: {
                            title: res.data.Title,
                            yor: res.data.Released
                        },
                        message: ''
                    });
                }
                else {
                    this.setState( {
                        results: {},
                        message: 'Movie not found.'
                    });
                }
            })
            .catch( error => {
                if ( error ) {
                    this.setState( {
                        message: 'Failed to fetch data. Check your network connection.'
                    });
                } 
            })
    }

    inputOnChangeHandler = ( event ) => {
        const query = event.target.value;
        this.setState({
            query: query,
            message: ''
        });
    }

    render() {
        const { query } = this.state;
        return (
            <div className="page-container">
                <div className="header-container">
                    <h1 className="header">The Shoppies:<br></br>Movie Awards for Entrepreneurs</h1>
                    <h2 className="note">Nominate your 5 favorite movies!</h2>
                </div>
                <div className="search-container">
                    <div className="label-container">
                        <p className="title">Search Movie by Title</p>
                        <label className="search-label" htmlFor="search-input">
                            <i className="fas fa-search icon"></i>
                            <span>
                                <input
                                type="text"
                                name="query"
                                value={query}
                                id="search-input"
                                placeholder="Movie Title (e.g. Titanic)"
                                onChange={ this.inputOnChangeHandler }
                                />
                            </span>
                            <span>
                                <button onClick={ this.fetchSearchResult }>Search</button>
                            </span>
                        </label>
                    </div>
                </div>
                {/* Result */}
                    {this.renderSearchResults()}
            </div>
        )
    }
}

export default Search;
