import React from 'react';
import './NominateBut.css';

class NominateBut extends React.Component {
    constructor( props ) {
        super( props );
        this.toggle = this.toggle.bind(this);
        this.state = {
            active: true
        };
    }

    toggle() {
        const { active } = this.state;
        this.setState({ active: !active });
    };

    render() {
        return (
            <button disabled={ !this.state.active } className={this.state.active ? 'button' : 'inactive button'} onClick={ this.toggle }>Nominate</button>
        )
    }
}

export default NominateBut;
