import React from 'react';
import './NominateBut.css';
import { useDispatch } from 'react';

class NominateBut extends React.Component {
    constructor( props ) {
        super( props );
        // this.toggle = this.toggle.bind(this);
        this.state = {
            action: this.props.action,
            imdbID: this.props.imdbID,
            title: this.props.title,
            yor: this.props.yor,
            active: this.props.active
        };
    }

    toggle() {
        const { active } = this.state;
        this.setState({ active: !active });
    };

    render() {
        return (
            <button disabled={ !this.state.active } className={this.state.active ? 'active button' : 'inactive button'} onClick={ this.toggle  }> {this.state.imdbID } </button>
        )
    }
}

export default NominateBut;
