import React from 'react';
import './NominateBut.css';

class NominateBut extends React.Component {
    constructor( props ) {
        super( props );
        this.toggle = this.toggle.bind(this);
        this.state = {
            action: this.props.action,
            imdbID: this.props.imdbID,
            title: this.props.title,
            yor: this.props.yor,
            active: this.props.active
        };
        this.state.action = this.state.action.bind(this);
    }

    toggle() {
        const { active } = this.state;
        this.setState({ active: !active });
    };

    onClick = () => {
        this.toggle();
        this.state.action( this.imdbID, this.title, this.yor );
    }

    render() {
        return (
            <button disabled={ !this.state.active } className={this.state.active ? 'active button' : 'inactive button'} onClick={ this.onClick }> {this.state.imdbID } </button>
        )
    }
}

export default NominateBut;
