import React from 'react';
import './Search.css';
import axios from 'axios';

class Search extends React.Component {

    // define Search with its properties here
    constructor( props ) {
        super( props );

        this.state = {
            query: '',
            results: {
                query: '',
                title: '',
                yor: ''
            },
            message: ''
        }

        this.movieIDs = [];
    }

    renderSearchResults = () => {
        const results = this.state.results;
        const query = results.query;
        console.warn(this.state.message);
        if ( !this.state.message.length && results.title.length ) {
            return (
                <div className="result-container">
                    <div className="header-container">
                        <h1>Results for "{query}"</h1>
                    </div>
                    <div className="result">
                        {results.title}
                        {results.yor}
                    </div>
                </div>
            )
        }
        else {
            if ( !this.state.message.length ) {
                return (
                    <div className="result-container">
                        <div className="header-container">
                            <h1>Search Results</h1>
                        </div>
                    </div>
                )
            }
            else {
                return (
                    <div className="result-container">
                        <div className="header-container">
                            {this.state.message}
                        </div>
                    </div>
                )
            }
        }
    }

    fetchSearchResult = ( event ) => {
        const query = this.state.query;
        if ( query.length ) {
            const searchURL = `http://www.omdbapi.com/?apikey=7353c7c5&t=${ query }`;
            axios.get( searchURL )
                .then( res => {
                    console.warn(res.data);
                    if ( res.data.Title ) {
                        this.setState( {
                            results: {
                                query: query,
                                title: res.data.Title,
                                yor: res.data.Released
                            },
                            message: ''
                        });
                    }
                    else {
                        this.setState( {
                            results: {},
                            message: 'Movie not found.'
                        });
                    }
                })
                .catch( error => {
                    if ( error ) {
                        this.setState( {
                            message: 'Failed to fetch data. Check your network connection.'
                        });
                    } 
                })
        }
        else {
            this.setState( {
                message: 'Movie name cannot be empty.'
            });
        }
    }

    inputOnChangeHandler = ( event ) => {
        const query = event.target.value;
        this.setState({
            result: {query: query},
            query: query,
            message: ''
        });
    }

    render() {
        const { query } = this.state;
        return (
            <div className="page-container">
                <div className="header-container">
                    <h1 className="header">The Shoppies:<br></br>Movie Awards for Entrepreneurs</h1>
                    <h2 className="note">Nominate Your Top 5 Favorite Movies!</h2>
                </div>
                <div className="search-container">
                    <div className="label-container">
                        <p className="title">Search Movie by Title</p>
                        <label className="search-label" htmlFor="search-input">
                            <i className="fas fa-search icon"></i>
                            <span>
                                <input
                                type="text"
                                name="query"
                                value={query}
                                id="search-input"
                                placeholder="Movie Title (e.g. Titanic)"
                                onChange={ this.inputOnChangeHandler }
                                />
                            </span>
                            <span>
                                <button onClick={ this.fetchSearchResult }>Search</button>
                            </span>
                        </label>
                    </div>
                </div>
                {/* Result */}
                    {this.renderSearchResults()}
            </div>
        )
    }
}

export default Search;
