import React from 'react';
import './NominateBut.css';

class NominateBut extends React.Component {
    constructor( props ) {
        super( props );
        this.toggle = this.toggle.bind(this);
        this.state = {
            action: this.props.action,
            nominees: this.props.nominees,
            imdbID: this.props.imdbID,
            title: this.props.title,
            yor: this.props.yor,
            active: this.props.active
        };
        this.state.action = this.state.action.bind(this);
    }

    toggle() {
        console.warn('hi')
        const { active } = this.state;
        this.setState({ active: !active });
    };

    onClick = ( event ) => {
        const { imdbID } = this.state;
        const { title } = this.state;
        const { yor } = this.state;
        this.state.action( imdbID, title, yor );
        this.toggle();
    }

    updateAndNotify = () => {
        if (this.state.nominees.has(this.state.imdbID)) {
            this.setState({
                active: false
            })
        }
        else {
            this.setState({
                active: true
            })
        }
    }

    render() {
        return (
            <button disabled={ !this.state.active || this.state.nominees.has(this.state.imdbID)} className={this.state.active && ! this.state.nominees.has(this.state.imdbID ) ? 'active button' : 'inactive button'} onClick={ this.onClick }> Nominate </button>
        )
    }
}

export default NominateBut;
