import React from 'react';
import './Search.css';
import axios from 'axios';

class Search extends React.Component {

    // define Search with its properties here
    constructor( props ) {
        super( props );

        this.state = {
            query: '',
            lastQuery: '',
            results: {},
            message: ''
        }

        this.movieIDs = [];
    }

    renderSearchResults = () => {
        const { results } = this.state;
        const { lastQuery } = this.state;
        console.warn(this.state.message);
        // if ( !this.state.message.length && Object.keys( results ) && results.length ) {
        return (
            <div className="result-container">
                <div className="header-container">
                    <h1 className="msg">Results for "{lastQuery}"</h1>
                </div>
                <div className="result">
                    <div key="row-header" className="row-header">
                        <span className="result-header left">Title</span>
                        <span className="result-header middle">Year of Released</span>
                        <span className="result-header right">Nominate</span>
                    </div>
                    { results.map( result => {
                        return (
                            <div key={ result.imdbID } className="result-row">
                                <span className="result-title left">{ result.Title }</span>
                                <span className="result-year middle">({ result.Year })</span>
                                <span><button className="button right" id="nominate">Nominate</button></span>
                            </div>
                        )
                    })}
                </div>
            </div>
        )
        // }
        // else {
        //     if ( ! this.state.message.length ) {
        //         return (
        //             <div className="result-container">
        //                 <div className="header-container">
        //                     <h1 className="msg">Search Result</h1>
        //                 </div>
        //             </div>
        //         )
        //     }
        //     else {
        //         return (
        //             <div className="result-container">
        //                 <div className="header-container">
        //                     <h1 className="msg red">{ this.state.message }</h1>
        //                 </div>
        //             </div>
        //         )
        //     }
        // }
    }

    fetchSearchResult = ( event ) => {
        const query = this.state.query;
        this.setState( {
            lastQuery: query
        });
        if ( query.length ) {
            const searchURL = `http://www.omdbapi.com/?apikey=7353c7c5&s=${ query }`;
            axios.get( searchURL )
                .then( res => {
                    console.warn(res.data.Search);
                    if ( res.data.Search ) {
                        this.setState( {
                            results: res.data.Search,
                            message: ''
                        });
                    }
                    else {
                        this.setState( {
                            results: {},
                            message: 'Movie not found.'
                        });
                    }
                })
                .catch( error => {
                    if ( error ) {
                        this.setState( {
                            results: {},
                            message: 'Failed to fetch data. Check your network connection.'
                        });
                    } 
                })
        }
        else {
            this.setState( {
                results: {},
                message: 'Movie name cannot be empty.'
            });
        }
    }

    inputOnChangeHandler = ( event ) => {
        const query = event.target.value;
        this.setState({
            query: query,
            message: ''
        });
    }

    render() {
        const { query } = this.state;
        return (
            <div className="page-container">
                <div className="header-container">
                    <h1 className="header">The Shoppies:<br></br>Movie Awards for Entrepreneurs</h1>
                    <h2 className="note">Nominate Your Top 5 Favorite Movies!</h2>
                </div>
                <div className="search-container">
                    <div className="label-container">
                        <p className="title">Search Movie by Title</p>
                        <label className="search-label" htmlFor="search-input">
                            <i className="fas fa-search icon"></i>
                            <span>
                                <input
                                type="text"
                                name="query"
                                value={query}
                                id="search-input"
                                placeholder="Movie Title (e.g. Titanic)"
                                onChange={ this.inputOnChangeHandler }
                                />
                            </span>
                            <span>
                                <button onClick={ this.fetchSearchResult } className="button" id="search">Search</button>
                            </span>
                        </label>
                    </div>
                </div>
                {/* Result */}
                    {this.renderSearchResults()}

                <div className="footer">
                    <p>Designed and Built by Karina Lei, 2021.</p>
                </div>
            </div>
        )
    }
}

export default Search;
